insert into src_data(api,table_name,field_name,field_type,comment,table_comment,api_comment, field_order, for_output) values ('API_Loan_FndLstRpmntPrdtByID','pAPI_Loan_RpmntPeriodicity','PeriodicityBrief','DSFULLNAME','Сокращение периодичности.. Сокращение периодичности. Данное поле содержит периодичность, введенную в настройках. Для ОД если значение поля "Погашение ОД" на договоре выставлено равным: "Аннуитет" или "ОД равными суммами", возвращаемые возможные значения: "День", "Неделя", "Месяц", "Год". Если значение поля "Погашение ОД" на договоре "По графику", то возвращается значение "по графику". Если значение поля "Погашение ОД" на договоре "До востребования", то возвращается значение "до востребования". Для процентов  возвращаемые возможные значения: "День", "Неделя", "Месяц", "Год".','Таблица периодичности платежей','Предоставление информации о периодичности погашения ОД и уплаты процентов по кредиту',3,0);
insert into src_data(api,table_name,field_name,field_type,comment,table_comment,api_comment, field_order, for_output) values ('API_Loan_FndLstRpmntPrdtByID','pAPI_Loan_RpmntPeriodicity','PeriodsNumber','DSINT_KEY','Количество периодов. Поле определяет количество периодов, указанных в поле "Периодичность", через которые происходит уплата. Для ОД если значение поля "Погашение ОД" на договоре выставлено равным "Аннуитет" или "ОД равными суммами", возвращается значение из секции "Период" поля "Значение" на схеме периодичности. Если значение "Период" равно 0, возвращать 1. Если значение поля "Погашение ОД" на договоре выставлено равным: "По графику" или "До востребования", возвращается 0. Для процентов возвращается значение из секции "Период" поля "Значение" на схеме периодичности. Если значение "Период" равно 0, возвращать 1.','Таблица периодичности платежей','Предоставление информации о периодичности погашения ОД и уплаты процентов по кредиту',4,0);
